import React,{useState} from 'react';
import { useHistory,useParams } from 'react-router-dom';
import UserService from '../serivces/UserService';

const SignUp = () => {

  const[firstName,setFirstName] = useState('') 
  const[lastName,setLastName] = useState('')
  const[email,setEmail] = useState('')
  const[password,setPassword] = useState('')

  const saveUser = (e) =>{
    const user = {firstName,lastName,email,password}
    UserService.createUser(user).then((response)=>
    {
      console.log("User Data Sent")
      console.log(response.data)
      history.pushState('/verify')
    }).catch(error =>{
      console.log(error)
    })
  }


    return(
        <div className='container-fluid' style={{ width: 700, marginTop: 30 }}>
<link
  rel="stylesheet"
  href="https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/css/bootstrap.min.css"
  integrity="sha384-1BmE4kWBq78iYhFldvKuhfTAU6auU8tT94WrHftjDbrCEXSU1oBoqyl2QvZ6jIW3"
  crossOrigin="anonymous"
/>
<form>
<div className="form-outline mb-4 pt-100">
    <input type="text" id="firstName" className="form-control" placeholder="First Name" 
      value={firstName}
      onChange={(e)=>setFirstName(e.target.value)}
      required />
    <label className="form-label" htmlFor="firstName">
      First Name
    </label>
  </div>
  <div className="form-outline mb-4 pt-100">
    <input type="text" id="lastName" className="form-control "placeholder="Last Name"
      value={lastName}
      onChange={(e)=>setLastName(e.target.value)}
      required />
    <label className="form-label" htmlFor="lastName">
      Last Name
    </label>
  </div>
  {/* Email input */}
  <div className="form-outline mb-4 pt-100">
    <input type="email" id="form2Example1" className="form-control" placeholder="Email ID" 
      value={email}
      onChange={(e)=>setEmail(e.target.value)}
      required/>
    <label className="form-label" htmlFor="form2Example1">
      Email address
    </label>
  </div>
  {/* Password input */}
  <div className="form-outline mb-4">
    <input type="password" id="form2Example2" className="form-control" placeholder="Password" 
      value={password}
      onChange={(e)=>setPassword(e.target.value)}
      required />
    <label className="form-label" htmlFor="form2Example2">
      Password
    </label>
  </div>
  {/* 2 column grid layout for inline styling */}
  <div className="row mb-4">
    <div className="col d-flex justify-content-center">
      <button type="button" className="btn btn-primary btn-block mb-4" onClick={(e) => saveUser(e)}>
    Sign up
     </button>
      </div>

  </div>
  {/* Submit button */}

</form>


</div>
    )

}


export default SignUp;
